import React, { Component } from "react";
import Form from 'react-bootstrap/Form';
import DestinationDataService from "../services/destination.service";

export default class AddDestination extends Component {
  constructor(props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeStartDate = this.onChangeStartDate.bind(this);
    this.onChangeEndDate = this.onChangeEndDate.bind(this);
    this.onChangeTagName = this.onChangeTagName.bind(this); 
    this.addTag = this.addTag.bind(this);
    this.saveDestination = this.saveDestination.bind(this);
    this.newDestination = this.newDestination.bind(this);

    this.state = {
      id: null,
      title: "",
      description: "", 
      published: false,
      price: 0,
      startdate: null,
      enddate: null,
      tags:[],
      tagName:"",

      submitted: false
    };
  }

  onChangeTitle(e) {
    this.setState({
      title: e.target.value
    });
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    });
  }

  onChangePrice(e) {
    this.setState({
      price: e.target.value
    });
  }

  onChangeStartDate(e) {
    this.setState({
      startdate: e.target.value
    });
  }

  onChangeEndDate(e) {
    this.setState({
      enddate: e.target.value
    });
  }

  onChangeTagName(e) {

    this.setState({
      tagName: e.target.value
    });
  }

  addTag(e) {
      this.setState({
        tags: [...this.state.tags, { name: e}]
      });

      this.setState({
        tagName: ""
      });
  }


  saveDestination() {
    var data = {
      title: this.state.title,
      description: this.state.description,
      price: this.state.price,
      startdate: this.state.startdate,
      enddate: this.state.enddate,
      tags: this.state.tags
    };

    DestinationDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          title: response.data.title,
          description: response.data.description,
          published: response.data.published,
          price: response.data.price,
          startdate: response.data.startdate,
          enddate: response.data.enddate,
          tags: response.data.tags,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newDestination() {
    this.setState({
      id: null,
      title: "",
      description: "",
      published: false,
      price: 0,
      startdate: null,
      enddate: null,
      tags: [],

      submitted: false
    });
  }

  render() {

    const { tagName } = this.state;
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newDestination}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                className="form-control"
                id="title"
                required
                value={this.state.title}
                onChange={this.onChangeTitle}
                name="title"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={this.state.description}
                onChange={this.onChangeDescription}
                name="description"
              />
            </div>

            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input
                type="number"
                className="form-control"
                id="price"
                value={this.state.price}
                onChange={this.onChangePrice}
                name="price"
              />
            </div>

            <div className="form-group">
              <label htmlFor="startdate">StartDate</label>
              <input
                type="date"
                className="form-control"
                id="startdate"
                value={this.state.startdate}
                onChange={this.onChangeStartDate}
                name="startdate"
              />
            </div>

            <div className="form-group">
              <label htmlFor="enddate">EndDate</label>
              <input
                type="date"
                className="form-control"
                id="enddate"
                value={this.state.enddate}
                onChange={this.onChangeEndDate}
                name="enddate"
              />
            </div>

            <div className="form-group">
                <label htmlFor="tagName">Tags</label>
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    id="tagName"
                    value={tagName}
                    onChange={this.onChangeTagName}
                  />
                    <button className="btn btn-outline-secondary" 
                    type="button"
                    onClick={() => this.addTag(tagName)}
                    >
                      Add
                    </button>

                </div>
                <div className="d-flex edit-tags-list">
                  {this.state.tags.length > 0 && this.state.tags.map((tag) => (
                  <div className="badge text-bg-secondary" key={tag.id}>
                    {tag.name}
                  </div>
                  ))}
                </div>
              </div>


            <button onClick={this.saveDestination} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
