import React, { Component } from "react";
import TripDataService from "../services/trip.service";
import { withRouter } from '../common/with-router';

class Trip extends Component {
  constructor(props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.getTrip = this.getTrip.bind(this);
    this.onChangeBudget = this.onChangeBudget.bind(this);
    this.onChangeStartDate = this.onChangeStartDate.bind(this);
    this.onChangeEndDate = this.onChangeEndDate.bind(this);
    this.updateTrip = this.updateTrip.bind(this);
    this.deleteTrip = this.deleteTrip.bind(this);

    this.state = {
      currentTrip: {
        id: null,
      title: "",
      description: "", 
      budget: 0,
      startdate: null,
      enddate: null
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getTrip(this.props.router.params.id);
  }

  onChangeTitle(e) {
    const title = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTrip: {
          ...prevState.currentTrip,
          title: title
        }
      };
    });
  }

  onChangeDescription(e) {
    const description = e.target.value;

    this.setState(prevState => ({
      currentTrip: {
        ...prevState.currentTrip,
        description: description
      }
    }));
  }

  onChangeBudget(e) {
    const budget = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTrip: {
          ...prevState.currentTrip,
          budget: budget
        }
      };
    });
  }

  onChangeStartDate(e) {
    const startdate = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTrip: {
          ...prevState.currentTrip,
          startdate: startdate
        }
      };
    });
  }

  onChangeEndDate(e) {
    const enddate = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTrip: {
          ...prevState.currentTrip,
          enddate: enddate
        }
      };
    });
  }

  getTrip(id) {
    TripDataService.get(id)
      .then(response => {
        this.setState({
          currentTrip: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateTrip() {
    TripDataService.update(
      this.state.currentTrip.id,
      this.state.currentTrip
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The trip was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteTrip() {    
    TripDataService.delete(this.state.currentTrip.id)
      .then(response => {
        console.log(response.data);
        this.props.router.navigate('/trips');
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentTrip } = this.state;

    return (
      <div>
        {currentTrip ? (
          <div className="edit-form">
            <h4>Trip</h4>
            <form>
              <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                  type="text"
                  className="form-control"
                  id="title"
                  value={currentTrip.title}
                  onChange={this.onChangeTitle}
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea 
                  type="text"
                  class="form-control" 
                  id="description" 
                  rows="4"
                  value={currentTrip.description}
                  onChange={this.onChangeDescription}
                >
                </textarea>
              </div>
              <div className="form-group">
                <label htmlFor="budget">Budget</label>
                <input
                  type="number"
                  className="form-control"
                  id="budget"
                  value={currentTrip.budget}
                  onChange={this.onChangeBudget}
                />
              </div>

              <div className="form-group">
                <label htmlFor="startdate">StartDate</label>
                <input
                  type="date"
                  className="form-control"
                  id="startdate"
                  value={currentTrip.startdate}
                  onChange={this.onChangeStartDate}
                />
              </div>

              <div className="form-group">
                <label htmlFor="enddate">EndDate</label>
                <input
                  type="date"
                  className="form-control"
                  id="enddate"
                  value={currentTrip.enddate}
                  onChange={this.onChangeEndDate}
                />
              </div>
            </form>
            <div class="d-flex justify-content-around py-2">

              <button
                className="btn btn-danger"
                onClick={this.deleteTrip}
              >
                Delete
              </button>

              <button
                type="submit"
                className="btn btn-success"
                onClick={this.updateTrip}
              >
                Update
              </button>
              <p>{this.state.message}</p>
            </div>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a Trip...</p>
          </div>
        )}
      </div>
    );
  }
}

export default withRouter(Trip);
